import axios from "axios";
import React, { useEffect, useRef } from "react";
import { useNavigate, useParams } from "react-router-dom";
// import Api from "./Api";

const Update=()=>
{   
    var textname = useRef();
    var textemail = useRef();
    var textpassword = useRef();
    var navigate = useNavigate();
    var {id}  = useParams();
    useEffect((e)=>{
        var a = new FormData
        a.set('id',id);

        axios.post("https://geton.yarainfotech.com/single-data.php",a).then(function(response){
            console.log(response.data);

            textname.current.value = response.data.name
            textemail.current.value = response.data.email
            textpassword.current.value = response.data.password
        })

    },[])
    const handleSubmit =(e)=>
    {
        e.preventDefault();
        // console.log(textname.current.value)
        var Name = textname.current.value
        var Email = textemail.current.value
        var Passsword= textpassword.current.value

        var a = new FormData();
        a.set("id",id)
        a.set("name",Name)
        a.set("email",Email)
        a.set("password",Passsword)
        axios.post("https://geton.yarainfotech.com/edit-data.php",a).then(
            function(response)
            {
                if(response.data.status =="true")
                {
                    navigate('/api');
                }
            }
        )


    }
    return(
        <>
            <form method="post" onSubmit={handleSubmit}>
                <table cellPadding={5} cellSpacing={5} border={2}>
                    <tr>
                        <td>name</td>
                        <td><input type="text" ref={textname}/></td>
                    </tr>
                    <tr>
                        <td>Email</td>
                        <td><input type="text" ref={textemail}/></td>
                    </tr>
                    <tr>
                        <td>password</td>
                        <td><input type="text" ref={textpassword}/></td>
                    </tr>
                    <tr>
                        <td><input type="submit" /></td>
                    </tr>
                </table>
            </form>

        </>
    );
}

export default Update;
