import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const DeleteApi = () => {
  var [data1, setData1] = useState([]);
  var navigate = useNavigate();
  useEffect(() => {
    fetchdata();
  }, []);
  function fetchdata() {
    axios
      .get("https://geton.yarainfotech.com/get-data.php")
      .then(function (response) {
        setData1(response.data);

        // console.log(response);
      });
  }
  const handledelete = (e) => {
    // console.log(e.target.getAttribute('getId'));
    var id = e.target.getAttribute("getId");

    var a = new FormData;
    a.set("id", id);

    axios
      .post("https://geton.yarainfotech.com/delete-data.php", a)
      .then(function (response) {
          console.log(response)
        if (response.data.status ==="true") {
          fetchdata();
        }
      });

    // console.log(a);
  };
  const handleupdate = (e) => {
    var id = e.target.getAttribute("getId");

    navigate("/update/" + id);
  };
  return (
    <>
      <table border={2} cellPadding={7} cellSpacing={2}>
        <tr>
          <th>created_at</th>
          <th>Id</th>
          <th>Email</th>
          <th>Name</th>
          <th>Password</th>
          {/* <th>Updated_at</th> */}
          <th>Delete</th>
          <th>update</th>
        </tr>

        {data1.map((e) => {
          console.log(e);
          return (
            <>
              <tr>
                <td>{e.created_at}</td>
                <td>{e.id}</td>
                <td>{e.email}</td>
                <td>{e.name}</td>
                <td>{e.password}</td>
                {/* <td>{e.updated_at}</td> */}
                <td>
                  <button onClick={handledelete} getId={e.id}>
                    delete
                  </button>
                </td>
                <td>
                  <button onClick={handleupdate} getId={e.id}>
                    update
                  </button>
                </td>
              </tr>
            </>
          );
        })}
      </table>
    </>
  );
};
export default DeleteApi;
