import axios from "axios";
import React, { useRef } from "react";
import { useNavigate } from "react-router-dom";
// import Api from "./Api";

const LoginApi =()=>
{   
    var textemail = useRef();
    var textpassword = useRef();
    var navigate = useNavigate();

    const handleSubmit =(e)=>
    {
        // e.preventDefault();
        // console.log(textname.current.value)
        var Email = textemail.current.value
        var Passsword= textpassword.current.value

        var a = new FormData();

        a.set("email",Email)
        a.set("password",Passsword)

        axios.post("https://geton.yarainfotech.com/login-data.php",a).then(
            function(response)
            {
                if(response.data.status =="true")
                {
                    navigate('/api');
                    localStorage.setItem("name",response.data.userdata.name);
                }
                else
                {
                    alert("weong");
                }
            }
        )


    }
    return(
        <>
            <form method="post" onSubmit={handleSubmit}>
                <table cellPadding={5} cellSpacing={5} border={2}>
                    <tr>
                        <td>Email</td>
                        <td><input type="text" ref={textemail}/></td>
                    </tr>
                    <tr>
                        <td>password</td>
                        <td><input type="text" ref={textpassword}/></td>
                    </tr>
                    <tr>
                        <td><input type="submit" /></td>
                    </tr>
                </table>
            </form>

        </>
    );
}

export default LoginApi;
