import Componant2 from "./Componant2";
// import UserContext from "./UserContext";

const Componant1 = () => {
    const str = "  hello bhavya  ";
  return (
    <>
      <UserContext.Provider value={str}>
        <h1>This is Componant1</h1>
        <Componant2 />
      </UserContext.Provider>
    </>
  );
};
export default Componant1;
